    // })

    fetch(`http://localhost:3000/posts/${this.props.post.id}`, {
          method: "DELETE",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          }
        })
  }


    updatePost = (e) => {
        e.preventDefault();
        // e.target.reset();

        // const { title, cost, body };
        // const post = { title, cost, body };

        let updatedPost = {
          title: e.target.title.value,
          pic: e.target.pic.value,
          body: e.target.body.value, 
          cost: e.target.cost.value,
          user_id: this.props.user.id
        };

        fetch(`http://localhost:3000/posts/${this.props.post.id}`, {
          method: "DELETE",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify( updatedPost ),
        })
          // .then((r) => r.json())
          // .then((response) => {

          //   console.log(response);
          //   // localStorage.token = response.jwt;
          //   // this.props.setCurrentUser(response.user);
          // });
      };

      // Logic needed to render posts that user created
      // this.props.posts.user.id == this.props.user.id

    render(){
        return(
          <div className= "n">
                <h1>My Posts</h1>
                {/* {this.props.posts.user.id.map(post => <MyPost key={post.id} post={post} user={this.props.user} updatePost={this.updatePost} handleDelete={this.handleDelete}/>)} */}
                {this.props.posts.map(post => <MyPost key={post.id} post={post} user={this.props.user} updatePost={this.updatePost} handleDelete={this.handleDelete}/>)}

            </div>
        );
    }
}

export default MyPosts;
